import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import './App.css';

const ForgotPassword = () => {
  const [step, setStep] = useState(1); // 1=email, 2=otp, 3=reset, 4=done
  const [email, setEmail] = useState('');
  const [sending, setSending] = useState(false);
  const [otp, setOtp] = useState('');
  const [verifying, setVerifying] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [newPass, setNewPass] = useState('');
  const [conPass, setConPass] = useState('');
  const [resetting, setResetting] = useState(false);

  const navigate = useNavigate();

  // 1. Send OTP to email
  const handleSendOtp = async (e) => {
    e.preventDefault();
    if (!email) {
      toast.error("Please enter your email address.");
      return;
    }
    setSending(true);
    try {
      const response = await fetch('/api/auth/forgot-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }), // backend should send OTP to this email
      });
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to send reset email.');
      }
      setSent(true);
      toast.success("If this email is registered, you will receive a reset link shortly.");
    } catch (err) {
      toast.error(err.message || 'Error sending reset email');
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="auth-shell">
      <div className="auth-card">
        <h2 style={{ marginBottom: 8 }}>Forgot Password</h2>
        <p className="page-subtitle" style={{ marginBottom: 24 }}>
          Enter your email address and we'll send you a link to reset your password.
        </p>
        {sent ? (
          <div style={{ textAlign: 'center', marginBottom: 20, color: 'var(--primary)' }}>
            If your email is registered, a reset link has been sent.
          </div>
        ) : (
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 16 }}>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                required
                placeholder="Enter your email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                autoFocus
              />
            </div>
            <button type="submit" className="primary-btn" style={{ width: '100%' }} disabled={sending}>
              {sending ? "Sending..." : "Send Reset Link"}
            </button>
          </form>
        )}
        <div style={{ marginTop: 16, textAlign: 'center' }}>
          <Link to="/login" style={{ color: "var(--primary)" }}>Back to Login</Link>
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
