import React, { useState } from "react";
import api from "../utils/api"; // Use your axios instance for auth & baseURL
import { toast } from "react-toastify";

export default function PerformanceReviewForm() {
    const [step, setStep] = useState(1);

    const [formData, setFormData] = useState({
        employeeDetails: {
            name: "",
            designation: "",
            department: "",
            reportingManager: "",
            reviewPeriod: "",
            dateOfReview: "",
        },
        responsibilities: [""],
        performance: [
            { category: "Work Quality", rating: "", managerComments: "" },
            { category: "Task Completion & Productivity", rating: "", managerComments: "" },
            { category: "Technical Skills", rating: "", managerComments: "" },
            { category: "Communication", rating: "", managerComments: "" },
            { category: "Team Collaboration", rating: "", managerComments: "" },
            { category: "Problem-Solving", rating: "", managerComments: "" },
            { category: "Ownership & Responsibility", rating: "", managerComments: "" },
            { category: "Time Management", rating: "", managerComments: "" },
            { category: "Learning & Improvement", rating: "", managerComments: "" },
        ],
    });

    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});

    // Validation logic
    const validateSection1 = () => {
        const errs = {};
        const fields = formData.employeeDetails;
        if (!fields.name.trim()) errs.name = "Required";
        if (!fields.designation.trim()) errs.designation = "Required";
        if (!fields.department.trim()) errs.department = "Required";
        if (!fields.reportingManager.trim()) errs.reportingManager = "Required";
        if (!fields.reviewPeriod.trim()) errs.reviewPeriod = "Required";
        if (!fields.dateOfReview) errs.dateOfReview = "Required";
        if (!formData.responsibilities.length || formData.responsibilities.some((r) => !r.trim()))
            errs.responsibilities = "All responsibilities are required";
        return errs;
    };

    const validateSection2 = () => {
        const perfErr = [];
        formData.performance.forEach((item, idx) => {
            if (!item.rating || ![1, 2, 3, 4, 5].includes(Number(item.rating))) {
                perfErr[idx] = "Rating required (1–5)";
            }
        });
        return { performance: perfErr };
    };

    const handleEmployeeChange = (e) => {
        setFormData((prev) => ({
            ...prev,
            employeeDetails: {
                ...prev.employeeDetails,
                [e.target.name]: e.target.value,
            },
        }));
        setErrors((errs) => ({ ...errs, [e.target.name]: undefined }));
    };

    const handleResponsibilityChange = (index, value) => {
        const updated = [...formData.responsibilities];
        updated[index] = value;
        setFormData((prev) => ({ ...prev, responsibilities: updated }));
        setErrors((errs) => ({ ...errs, responsibilities: undefined }));
    };

    const removeResponsibility = (index) => {
        const updated = [...formData.responsibilities];
        updated.splice(index, 1);
        setFormData((prev) => ({ ...prev, responsibilities: updated }));
    };

    const handlePerformanceChange = (index, key, value) => {
        const updated = [...formData.performance];
        updated[index][key] = value;
        setFormData({ ...formData, performance: updated });
    };

    const addResponsibility = () => {
        setFormData({
            ...formData,
            responsibilities: [...formData.responsibilities, ""],
        });
    };

    const submitForm = async () => {
        try {
            await axios.post("http://localhost:5000/api/review/create", formData);
            alert("Review submitted successfully!");
        } catch (err) {
            console.error(err);
            alert("Failed to submit");
        }
    };

    return (
        <div style={{ maxWidth: "900px", margin: "0 auto", padding: "20px" }}>

            {/* ------------------------ SECTION 1 ------------------------ */}
            {step === 1 && (
                <div>
                    <h2>Employee Details</h2>

                    <input type="text" name="name" placeholder="Name"
                        value={formData.employeeDetails.name} onChange={handleEmployeeChange}
                    /><br /><br />

                    <input type="text" name="designation" placeholder="Designation"
                        value={formData.employeeDetails.designation} onChange={handleEmployeeChange}
                    /><br /><br />

                    <input type="text" name="department" placeholder="Department"
                        value={formData.employeeDetails.department} onChange={handleEmployeeChange}
                    /><br /><br />

                    <input type="text" name="reportingManager" placeholder="Reporting Manager"
                        value={formData.employeeDetails.reportingManager} onChange={handleEmployeeChange}
                    /><br /><br />

                    <select name="reviewPeriod"
                        value={formData.employeeDetails.reviewPeriod}
                        onChange={handleEmployeeChange}
                    >
                        <option value="">Select Review Period</option>
                        <option value="Monthly">Monthly</option>
                        <option value="Quarterly">Quarterly</option>
                    </select>
                    <br /><br />

                    <input
                        type="date"
                        name="dateOfReview"
                        value={formData.employeeDetails.dateOfReview}
                        onChange={handleEmployeeChange}
                    />
                    <br /><br />

                    <h3>Key Responsibilities</h3>
                    {formData.responsibilities.map((res, idx) => (
                        <div key={idx}>
                            <input
                                type="text"
                                placeholder={`Responsibility ${idx + 1}`}
                                value={res}
                                onChange={(e) =>
                                    handleResponsibilityChange(idx, e.target.value)
                                }
                            />
                        </div>
                    ))}
                    <button onClick={addResponsibility}>+ Add More</button>

                    <br /><br />
                    <button onClick={() => setStep(2)}>Next ➝</button>
                </div>
            )}

            {/* ------------------------ SECTION 2 ------------------------ */}
            {step === 2 && (
                <div>
                    <h2>Performance Overview (Ratings 1–5)</h2>

                    <table border="1" width="100%" cellPadding="8">
                        <thead>
                            <tr>
                                <th>Category</th>
                                <th>Rating</th>
                                <th>Manager Comments</th>
                            </tr>
                        </thead>
                        <tbody>
                            {formData.performance.map((item, idx) => (
                                <tr key={idx}>
                                    <td>{item.category}</td>
                                    <td>
                                        <select
                                            value={item.rating}
                                            onChange={(e) =>
                                                handlePerformanceChange(idx, "rating", e.target.value)
                                            }
                                        >
                                            <option value="">Select</option>
                                            {[1, 2, 3, 4, 5].map((num) => (
                                                <option key={num} value={num}>
                                                    {num}
                                                </option>
                                            ))}
                                        </select>
                                    </td>
                                    <td>
                                        <input
                                            type="text"
                                            value={item.managerComments}
                                            onChange={(e) =>
                                                handlePerformanceChange(idx, "managerComments", e.target.value)
                                            }
                                            placeholder="Comments"
                                        />
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    <br />
                    <button onClick={() => setStep(1)}>← Back</button>
                    <button onClick={submitForm} style={{ marginLeft: "10px" }}>
                        Submit Review
                    </button>
                </div>
            )}

        </div>
    );
}
