import React, { useState } from "react";
import api from "../utils/api"; // Use your axios instance for auth & baseURL
import { toast } from "react-toastify";

export default function PerformanceReviewForm() {
    const [step, setStep] = useState(1);

    const [formData, setFormData] = useState({
        employeeDetails: {
            name: "",
            designation: "",
            department: "",
            reportingManager: "",
            reviewPeriod: "",
            dateOfReview: "",
        },
        responsibilities: [""],
        performance: [
            { category: "Work Quality", rating: "", managerComments: "" },
            { category: "Task Completion & Productivity", rating: "", managerComments: "" },
            { category: "Technical Skills", rating: "", managerComments: "" },
            { category: "Communication", rating: "", managerComments: "" },
            { category: "Team Collaboration", rating: "", managerComments: "" },
            { category: "Problem-Solving", rating: "", managerComments: "" },
            { category: "Ownership & Responsibility", rating: "", managerComments: "" },
            { category: "Time Management", rating: "", managerComments: "" },
            { category: "Learning & Improvement", rating: "", managerComments: "" },
        ],
    });

    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});

    // Validation logic
    const validateSection1 = () => {
        const errs = {};
        const fields = formData.employeeDetails;
        if (!fields.name.trim()) errs.name = "Required";
        if (!fields.designation.trim()) errs.designation = "Required";
        if (!fields.department.trim()) errs.department = "Required";
        if (!fields.reportingManager.trim()) errs.reportingManager = "Required";
        if (!fields.reviewPeriod.trim()) errs.reviewPeriod = "Required";
        if (!fields.dateOfReview) errs.dateOfReview = "Required";
        if (!formData.responsibilities.length || formData.responsibilities.some((r) => !r.trim()))
            errs.responsibilities = "All responsibilities are required";
        return errs;
    };

    const validateSection2 = () => {
        const perfErr = [];
        formData.performance.forEach((item, idx) => {
            if (!item.rating || ![1, 2, 3, 4, 5].includes(Number(item.rating))) {
                perfErr[idx] = "Rating required (1–5)";
            }
        });
        return { performance: perfErr };
    };

    const handleEmployeeChange = (e) => {
        setFormData((prev) => ({
            ...prev,
            employeeDetails: {
                ...prev.employeeDetails,
                [e.target.name]: e.target.value,
            },
        }));
        setErrors((errs) => ({ ...errs, [e.target.name]: undefined }));
    };

    const handleResponsibilityChange = (index, value) => {
        const updated = [...formData.responsibilities];
        updated[index] = value;
        setFormData((prev) => ({ ...prev, responsibilities: updated }));
        setErrors((errs) => ({ ...errs, responsibilities: undefined }));
    };

    const removeResponsibility = (index) => {
        const updated = [...formData.responsibilities];
        updated.splice(index, 1);
        setFormData((prev) => ({ ...prev, responsibilities: updated }));
    };

    const handlePerformanceChange = (index, key, value) => {
        const updated = [...formData.performance];
        updated[index][key] = value;
        setFormData((prev) => ({ ...prev, performance: updated }));
        if (key === "rating") {
            setErrors((errs) => {
                const updateErr = { ...errs };
                if (updateErr.performance) {
                    updateErr.performance[index] = undefined;
                }
                return updateErr;
            });
        }
    };

    const addResponsibility = () => {
        setFormData((prev) => ({
            ...prev,
            responsibilities: [...prev.responsibilities, ""],
        }));
    };

    const handleNext = () => {
        const err = validateSection1();
        setErrors(err);
        if (Object.keys(err).length === 0) setStep(2);
    };

    const handleBack = () => setStep(1);

    const submitForm = async () => {
        const perfErrs = validateSection2();
        let sectionInvalid = false;
        if (perfErrs.performance.some((e) => e)) sectionInvalid = true;
        setErrors(perfErrs);
        if (sectionInvalid) return;

        setLoading(true);
        try {
            await api.post("/api/review/create", formData);
            toast.success("Review submitted successfully!");
            setFormData({
                employeeDetails: {
                    name: "",
                    designation: "",
                    department: "",
                    reportingManager: "",
                    reviewPeriod: "",
                    dateOfReview: "",
                },
                responsibilities: [""],
                performance: [
                    { category: "Work Quality", rating: "", managerComments: "" },
                    { category: "Task Completion & Productivity", rating: "", managerComments: "" },
                    { category: "Technical Skills", rating: "", managerComments: "" },
                    { category: "Communication", rating: "", managerComments: "" },
                    { category: "Team Collaboration", rating: "", managerComments: "" },
                    { category: "Problem-Solving", rating: "", managerComments: "" },
                    { category: "Ownership & Responsibility", rating: "", managerComments: "" },
                    { category: "Time Management", rating: "", managerComments: "" },
                    { category: "Learning & Improvement", rating: "", managerComments: "" },
                ],
            });
            setStep(1);
        } catch (err) {
            if (err.response && err.response.data && err.response.data.error) {
                toast.error(err.response.data.error);
            } else {
                toast.error("Failed to submit review.");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-shell">
            <div className="auth-card" style={{width:"500px"}}>
                < h1 className="page-subtitle" style={{ marginBottom: 24 }
                }>
                    Performance Review
                </h1 >
                <div style={{ marginBottom: 25 }}>
                    <div style={{ display: "flex", gap: 10 }}>
                        <div style={{
                            flex: 1,
                            fontWeight: step === 1 ? 700 : 500,
                            color: step === 1 ? "#356FC4" : "#888",
                            borderBottom: step === 1 ? "2px solid #356FC4" : "1px solid #eee",
                            paddingBottom: 5,
                            textAlign: "center",
                            transition: "all 0.15s"
                        }}>
                            1. Employee & Role
                        </div>
                        <div style={{
                            flex: 1,
                            fontWeight: step === 2 ? 700 : 500,
                            color: step === 2 ? "#356FC4" : "#888",
                            borderBottom: step === 2 ? "2px solid #356FC4" : "1px solid #eee",
                            paddingBottom: 5,
                            textAlign: "center",
                            transition: "all 0.15s"
                        }}>
                            2. Performance
                        </div>
                    </div>
                </div>

                {/* SECTION 1 */}
                {
                    step === 1 && (
                        <form
                            onSubmit={e => { e.preventDefault(); handleNext(); }}
                            style={{ display: "grid", gap: 24 }}
                            noValidate
                        >
                            {/* Employee Fields */}
                            <div style={{ display: "grid", gap: 14 }}>
                                <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 18 }}>
                                    <div className="form-group">
                                        <label className="form-label">Name<span style={{ color: '#e33' }}>*</span></label>
                                        <input
                                            type="text" name="name"
                                            className="form-input"
                                            placeholder="Full Name"
                                            autoComplete="off"
                                            value={formData.employeeDetails.name}
                                            onChange={handleEmployeeChange}
                                            style={{ width: "100%" }}
                                        />
                                        {errors.name && <div className="form-error">{errors.name}</div>}
                                    </div>
                                    <div className="form-group">
                                        <label className="form-label">Designation<span style={{ color: '#e33' }}>*</span></label>
                                        <input
                                            type="text" name="designation"
                                            className="form-input"
                                            placeholder="Designation/Title"
                                            autoComplete="off"
                                            value={formData.employeeDetails.designation}
                                            onChange={handleEmployeeChange}
                                            style={{ width: "100%" }}
                                        />
                                        {errors.designation && <div className="form-error">{errors.designation}</div>}
                                    </div>
                                </div>
                                <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 18 }}>
                                    <div className="form-group">
                                        <label className="form-label">Department<span style={{ color: '#e33' }}>*</span></label>
                                        <input
                                            type="text" name="department"
                                            className="form-input"
                                            placeholder="Department"
                                            autoComplete="off"
                                            value={formData.employeeDetails.department}
                                            onChange={handleEmployeeChange}
                                            style={{ width: "100%" }}
                                        />
                                        {errors.department && <div className="form-error">{errors.department}</div>}
                                    </div>
                                    <div className="form-group">
                                        <label className="form-label">Reporting Manager<span style={{ color: '#e33' }}>*</span></label>
                                        <input
                                            type="text" name="reportingManager"
                                            className="form-input"
                                            placeholder="Reporting Manager"
                                            autoComplete="off"
                                            value={formData.employeeDetails.reportingManager}
                                            onChange={handleEmployeeChange}
                                            style={{ width: "100%" }}
                                        />
                                        {errors.reportingManager && <div className="form-error">{errors.reportingManager}</div>}
                                    </div>
                                </div>
                                <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 18 }}>
                                    <div className="form-group">
                                        <label className="form-label">Review Period<span style={{ color: '#e33' }}>*</span></label>
                                        <select
                                            name="reviewPeriod"
                                            className="form-input"
                                            value={formData.employeeDetails.reviewPeriod}
                                            onChange={handleEmployeeChange}
                                            style={{ width: "100%", color: "#999" }}
                                        >
                                            <option value="">Select Period</option>
                                            <option value="Monthly">Monthly</option>
                                            <option value="Quarterly">Quarterly</option>
                                        </select>
                                        {errors.reviewPeriod && <div className="form-error">{errors.reviewPeriod}</div>}
                                    </div>
                                    <div className="form-group">
                                        <label className="form-label">Date of Review<span style={{ color: '#e33' }}>*</span></label>
                                        <input
                                            type="date"
                                            name="dateOfReview"
                                            className="form-input"
                                            value={formData.employeeDetails.dateOfReview}
                                            onChange={handleEmployeeChange}
                                            style={{ width: "100%", color: "#999" }}
                                        />
                                        {errors.dateOfReview && <div className="form-error">{errors.dateOfReview}</div>}
                                    </div>
                                </div>
                            </div>
                            {/* Responsibilities */}
                            <div className="form-group">
                                <label className="form-label">
                                    Key Responsibilities<span style={{ color: '#e33' }}>*</span>
                                </label>
                                <div style={{ display: 'flex', flexDirection: 'column', gap: 7 }}>
                                    {formData.responsibilities.map((res, idx) => (
                                        <div key={idx} style={{ display: 'flex', alignItems: 'center', gap: 5 }}>
                                            <input
                                                type="text"
                                                className="form-input"
                                                placeholder={`Responsibility ${idx + 1}`}
                                                value={res}
                                                autoComplete="off"
                                                onChange={e => handleResponsibilityChange(idx, e.target.value)}
                                                style={{ flex: 1 }}
                                            />
                                            {formData.responsibilities.length > 1 && (
                                                <button
                                                    type="button"
                                                    className="ghost-btn"
                                                    onClick={() => removeResponsibility(idx)}
                                                    title="Remove responsibility"
                                                    style={{
                                                        background: "none",
                                                        border: "none",
                                                        color: "#cc2222",
                                                        fontSize: 18,
                                                        cursor: "pointer",
                                                        padding: 0,
                                                        marginLeft: 2
                                                    }}
                                                >✕</button>
                                            )}
                                        </div>
                                    ))}
                                </div>
                                {errors.responsibilities && <div className="form-error">{errors.responsibilities}</div>}
                                <button
                                    type="button"
                                    className="ghost-btn"
                                    style={{ marginTop: 5, fontSize: 14, color: "#355fc5", cursor: "pointer" }}
                                    onClick={addResponsibility}
                                >+ Add Responsibility</button>
                            </div>
                            <div className="form-group">
                                <button type="submit" className="primary-btn" style={{ width: 160 }}>
                                    Next ➝
                                </button>
                            </div>
                        </form>
                    )
                }

                {/* SECTION 2 */}
                {
                    step === 2 && (
                        <form
                            onSubmit={e => { e.preventDefault(); if (!loading) submitForm(); }}
                            noValidate
                            style={{ display: "grid", gap: 24 }}
                        >
                            <div className="form-group">
                                <h2 style={{ marginBottom: 3, fontSize: '1.2rem', color: "#225", fontWeight: 700 }}>Performance Overview</h2>
                                <div style={{
                                    marginBottom: 9,
                                    color: "#666",
                                    fontSize: "15px"
                                }}>
                                    Rate each category (1–5, where 5 is best)
                                </div>
                            </div>

                            <div style={{ overflowX: 'auto' }}>
                                <table
                                    style={{
                                        width: "100%",
                                        borderCollapse: "collapse",
                                        fontSize: 15,
                                        boxShadow: "0 0.5px 4px #eaeaea"
                                    }}
                                >
                                    <thead>
                                        <tr style={{ background: "#f6f8fa" }}>
                                            <th style={{ padding: 8, textAlign: 'left', fontWeight: 600, border: "1px solid #e5e5e5" }}>Category</th>
                                            <th style={{ padding: 8, textAlign: 'left', fontWeight: 600, border: "1px solid #e5e5e5" }}>Rating<span style={{ color: '#e33' }}>*</span></th>
                                            <th style={{ padding: 8, textAlign: 'left', fontWeight: 600, border: "1px solid #e5e5e5" }}>Manager Comments</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {formData.performance.map((item, idx) => (
                                            <tr key={idx} style={{ background: idx % 2 === 1 ? "#f9fbfc" : "#fff" }}>
                                                <td style={{ padding: 8, border: "1px solid #e9e9e9" }}>
                                                    {item.category}
                                                </td>
                                                <td style={{ padding: 8, border: "1px solid #e9e9e9" }}>
                                                    <select
                                                        style={{ width: 105 }}
                                                        value={item.rating}
                                                        className="form-input"
                                                        onChange={e => handlePerformanceChange(idx, "rating", e.target.value)}
                                                    >
                                                        <option value="">– Select –</option>
                                                        {[1, 2, 3, 4, 5].map(num => (
                                                            <option key={num} value={num}>{num}</option>
                                                        ))}
                                                    </select>
                                                    {errors.performance && errors.performance[idx] && (
                                                        <div className="form-error">{errors.performance[idx]}</div>
                                                    )}
                                                </td>
                                                <td style={{ padding: 8, border: "1px solid #e9e9e9" }}>
                                                    <input
                                                        type="text"
                                                        className="form-input"
                                                        placeholder="Write manager's notes"
                                                        value={item.managerComments}
                                                        onChange={e => handlePerformanceChange(idx, "managerComments", e.target.value)}
                                                        style={{ width: "98%" }}
                                                    />
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                            <div style={{ display: "flex", gap: 14, justifyContent: "flex-end" }}>
                                <button
                                    type="button"
                                    className="ghost-btn"
                                    onClick={handleBack}
                                    style={{ width: 110, background: "#f0f2f8" }}
                                >← Back
                                </button>
                                <button
                                    type="submit"
                                    className="primary-btn"
                                    disabled={loading}
                                    style={{ width: 160, fontWeight: 600, opacity: loading ? 0.65 : 1 }}
                                >
                                    {loading ? "Submitting..." : "Submit Review"}
                                </button>
                            </div>
                        </form>
                    )
                }
            </div >
        </div>
    );
}
